<?phpnamespace GDO\Todo\tpl;use GDO\Table\GDT_ListItem;use GDO\Todo\GDO_Todo;use GDO\UI\GDT_Container;/** @var GDO_Todo $todo * */$li = GDT_ListItem::make('todo_' . $todo->getID())->gdo($todo);$li->creatorHeader(null, null, $todo->displayTitle());$cont = GDT_Container::make()->vertical();$cont->addField($todo->gdoColumn('todo_description'));$cont->addField($todo->gdoColumn('todo_priority'));$li->content($cont);echo $li->render();